import Register from "./Components/Register";
function App() {


  return (
    <>
      <section className="App">
        {success ? (
          <section>
            <h1>Success!</h1>
            <a style={{
              textDecoration: "underline", color: "whitesmoke", textAlign: "center"}} href="">Sign In</a>
          </section>
        ) : (
          <>
              {errMsg ? <p className="err-msg">{`${errMsg}`}</p> : null}
            <h1>Register</h1>
            <form onSubmit={handleSubmit}>
              <label htmlFor="username">
                Username :{" "}
                {user ? (
                  validName ? (
                    <FaCheck
                      style={{
                        color: "lightgreen",
                        marginBottom: "-0.1rem",
                      }}
                    />
                  ) : (
                    <FaTimes
                      style={{ color: "red", marginBottom: "-0.1rem" }}
                    />
                  )
                ) : null}
              </label>
              <input
                ref={userRef}
                id="username"
                placeholder="Enter Username"
                value={user}
                onChange={(e) => setUser(e.target.value)}
                onFocus={() => setUserFocus(true)}
                onBlur={() => setUserFocus(false)}
              />

              <label htmlFor="password">
                Password :{" "}
                {password ? (
                  validPassword ? (
                    <FaCheck
                      style={{
                        color: "lightgreen",
                        marginBottom: "-0.1rem",
                      }}
                    />
                  ) : (
                    <FaTimes
                      style={{ color: "red", marginBottom: "-0.1rem" }}
                    />
                  )
                ) : null}
              </label>
              <input
                id="password"
                type="password"
                placeholder="Enter Password"
                value={password}
                aria-describedby="pwd-note"
                onChange={(e) => setPassword(e.target.value)}
                onFocus={() => setPwdFocus(true)}
                onBlur={() => setPwdFocus(false)}
              />
              {password && pwdFocus && !validPassword && (
                <p className="pwd-note" id="pwd-note">
                  <li>Must contain atleast one special character</li>
                  <li>
                    Must contain atleast one UPPERCASE and lowercase character
                  </li>
                  <li>Must contain atleast one digit</li>
                  <li>Must contain 8-23 characters</li>
                </p>
              )}
              <label htmlFor="re-enter">
                Re-Enter Password:{" "}
                {matchPwd ? (
                  validMatch ? (
                    <FaCheck
                      style={{
                        color: "lightgreen",
                        marginBottom: "-0.1rem",
                      }}
                    />
                  ) : (
                    <FaTimes
                      style={{ color: "red", marginBottom: "-0.1rem" }}
                    />
                  )
                ) : null}
              </label>
              <input
                id="re-enter"
                type="password"
                placeholder="Enter Password Again"
                value={matchPwd}
                onChange={(e) => setMatchPwd(e.target.value)}
                onFocus={() => setMatchFocus(true)}
                onBlur={() => setMatchFocus(false)}
              />

              <button
                type="submit"
                disabled={
                  !validName || !validPassword || !validMatch ? true : false
                }
              >
                Sign Up
              </button>

              <p className="already">Already Registered?</p>
              <a className="sign-in" href="">
                Sign In
              </a>
            </form>
          </>
        )}
      </section>
    </>
  );
}

export default App;
